    <script>
        $('#buttonsubmit').click(function(e) {

            e.preventDefault();

            if ($('#buttonsubmit').val() == "BOOK NOW") {
                var formData = new FormData();

                $('#maindata').serializeArray().forEach(function(field) {
                    formData.append(field.name, field.value);
                });

                $.ajax({
                    method: 'post',
                    processData: false,
                    contentType: false,
                    cache: false,
                    data: formData,
                    url: "{{ route('books.store') }}",
                    success: function(result) {
                        window.location.href =
                            "{{ route('web_calander_data_show') }}";
                    }
                });
            }
        })
    </script>


    <script type="text/javascript">
        // When the user scrolls the page, execute myFunction
        window.onscroll = function() {
            myFunction()
        };

        // Get the header
        var header = document.getElementById("myHeader");

        // Get the offset position of the navbar
        var sticky = header.offsetTop;

        // Add the sticky class to the header when you reach its scroll position. Remove "sticky" when you leave the scroll position
        function myFunction() {
            if (window.pageYOffset > sticky) {
                document.getElementById("free-class").style.backgroundColor = "#8fd241";
                document.getElementById("free-class").style.color = "rgb(0 0 0)";
                document.getElementById("free-class").style.padding = "10px 20px";

                header.classList.add("sticky");

            } else {
                document.getElementById("free-class").style.backgroundColor = "#8fd241";
                document.getElementById("free-class").style.color = "rgb(0 0 0)";
                document.getElementById("free-class").style.padding = "10px 20px";
                header.classList.remove("sticky");
            }
        }
    </script>
